          allowfullscreen
          referrerpolicy="no-referrer-when-downgrade"
          src="https://www.google.com/maps/embed/v1/place?key=
          AIzaSyBz0QsIYpZo3ZJJIj-vQ0PktTGFM5vwTkM
          &q=United+States">
        </iframe>}
        <br />
        {geoval}
      </div>
    );
  }

  function SelectCountry() {
    [country, selectCountry] = useState("Select a Country");

    let handleCountryChange = (e) => {
      selectCountry(e.target.value)
    }

      return (
        <div className="App">
        {/* Displaying the value of country */}
        {country}
        <br />

        <select onChange={handleCountryChange}> 
          <option value="Select a Country"> -- Select a Country -- </option>
                {/* Mapping through each country object in our countries array
              and returning an option element with the appropriate attributes / values.
             */}
          {countryList.map((country) => <option value={country}>{country}</option>)}
        </select>
        </div>
      );
  }

  function geocode(request) {
    clear();
    geocoder
      .geocode(request)
      .then((result) => {
        const { results } = result;
        geoval = results[0].geometry.location;

        // map.setCenter(results[0].geometry.location);
        // marker.setPosition(results[0].geometry.location);
        // marker.setMap(map);
        // responseDiv.style.display = "block";
        // response.innerText = JSON.stringify(result, null, 2);
        // return results;
      })
      .catch((e) => {
        alert("Geocode was not successful for the following reason: " + e);
      });
  }

  return(
    <div>
      <MapDisplay />
      <SelectCountry />
      </div>
  );
}
