import React, { useState } from 'react';
import ReactDOM from 'react-dom/client';
import './index.css';
import {countryList} from './countries.js';
//import App from './App';
import reportWebVitals from './reportWebVitals';

function App() {
  var country = "United States";
  function MapDisplay() {
    return(         
      <div>          
        <iframe
          width="1200"
          height="900"
          //style="border:0"
          //key="AIzaSyBz0QsIYpZo3ZJJIj-vQ0PktTGFM5vwTkM"
          loading="lazy"
          allowfullscreen
          referrerpolicy="no-referrer-when-downgrade"
          src="https://www.google.com/maps/embed/v1/place?key=
          AIzaSyBz0QsIYpZo3ZJJIj-vQ0PktTGFM5vwTkM
          &q=Hudson+NH">
        </iframe>        
      </div>
    );
  }

  function SelectCountry() {
    let [{country}, selectCountry] = useState("Select a Country");

    let handleCountryChange = (e) => {
      selectCountry(e.target.value)
    }

      return (
        <div className="App">
        {/* Displaying the value of country */}
        {country}
        <br />

        <select onChange={handleCountryChange}> 
          <option value="Select a Country"> -- Select a Country -- </option>
                {/* Mapping through each country object in our countries array
              and returning an option element with the appropriate attributes / values.
             */}
          {countryList.map(({country}) => <option value={country}>{country}</option>)}
        </select>
        </div>
      );
  }

  return(
    <div>
      <MapDisplay />
      <SelectCountry />
      </div>
  );
}
