/* eslint-disable no-undef */
import React, { useState } from 'react';
import ReactDOM from 'react-dom/client';
import './index.css';
import {countryList} from './countries.js';
//import App from './App';
import reportWebVitals from './reportWebVitals';

let map;
let marker;
let geocoder;
let responseDiv;
let response;
let allPersons = [[]];

function initMap() {
  map = new google.maps.Map(document.getElementById("map"), {
    zoom: 3,
    center: { lat: 0, lng: 0 },
    mapTypeControl: false,
  });
  geocoder = new google.maps.Geocoder();

  const inputText = document.createElement("input");

  inputText.type = "text";
  inputText.id = "input-text";
  inputText.placeholder = "Enter a location";

  const submitButton = document.createElement("input");

  submitButton.type = "button";
  submitButton.value = "Geocode";
  submitButton.classList.add("button", "button-primary");

  const clearButton = document.createElement("input");

  clearButton.type = "button";
  clearButton.value = "Clear";
  clearButton.classList.add("button", "button-secondary");

  const addButton = document.createElement("input");

  addButton.type = "button";
  addButton.value = "Add to List";
  addButton.classList.add("button", "button-final");

  const nextButton = document.createElement("input");

  nextButton.type = "button";
  nextButton.value = "Next Person";
  nextButton.classList.add("button", "button-next");

  const doneButton = document.createElement("input");

  doneButton.type = "button";
  doneButton.value = "All Done!";
  doneButton.classList.add("button", "button-done");

  response = document.createElement("pre");
  response.id = "response";
  response.innerText = "";
  responseDiv = document.createElement("div");
  responseDiv.id = "response-container";
  responseDiv.appendChild(response);

  const instructionsElement = document.createElement("p");

  instructionsElement.id = "instructions";
  instructionsElement.innerHTML =
    "<strong>Instructions</strong>: Enter an address in the textbox to geocode or click on the map to reverse geocode.";
  map.controls[google.maps.ControlPosition.TOP_LEFT].push(inputText);
  map.controls[google.maps.ControlPosition.TOP_LEFT].push(submitButton);
  map.controls[google.maps.ControlPosition.TOP_LEFT].push(clearButton);
  map.controls[google.maps.ControlPosition.TOP_LEFT].push(addButton);
  map.controls[google.maps.ControlPosition.TOP_LEFT].push(nextButton);
  map.controls[google.maps.ControlPosition.TOP_LEFT].push(doneButton);
  map.controls[google.maps.ControlPosition.LEFT_TOP].push(instructionsElement);
  map.controls[google.maps.ControlPosition.LEFT_TOP].push(responseDiv);
  marker = new google.maps.Marker({
    map,
  });
  map.addListener("click", (e) => {
    geocode({ location: e.latLng });
  });
  submitButton.addEventListener("click", () =>
    geocode({ address: inputText.value }),
  );
  addButton.addEventListener("click", () =>
    addToList(),
  );
  clearButton.addEventListener("click", () => {
    clear();
  });
  clear();
}

function clear() {
  marker.setMap(null);
  responseDiv.style.display = "none";
}

function geocode(request) {
  clear();
  geocoder
    .geocode(request)
    .then((result) => {
      const { results } = result;

      map.setCenter(results[0].geometry.location);
      marker.setPosition(results[0].geometry.location);
      marker.setMap(map);
      let jsonData = JSON.parse(JSON.stringify(results[0], null, 2));
      alert("JSON DATA: " + jsonData);
      for (val in jsonData['results']) {
      //for (val in result) {
        alert("val is " + val[0]);
        for (address in val['address_components']) {
          alert("name is: " + address['long_name']);
            if(address['types'][0] == "country" && address['types'][1] == "political") {
              document.getElementById("input-text").value = address['long_name'];
            }
        }
    }
      //document.getElementById("input-text").value = results[0].address_components[3].long_name;
      responseDiv.style.display = "block";
      response.innerText = JSON.stringify(result, null, 2);
      return results;
    })
    .catch((e) => {
      alert("Geocode was not successful for the following reason: " + e);
    });
}

function addToList() {
  // Add from the input box a country or typed in city to visit
  let place = inputText.Value
}

window.initMap = initMap;

reportWebVitals();
